import { Component, OnInit, OnDestroy } from '@angular/core';

import { BehaviorSubject, Subscription } from 'rxjs';

import { DashboardService } from '../../services/dashboard.service';
import { Store } from 'src/app/core/state/app-store';
import { StatusCounts } from '../../models';
import { DashboardFilter } from 'src/app/shared/models/dto/stats/dashboard-filter';


interface DateRange {
    dateStart: Date;
    dateEnd: Date;
}

@Component({
    selector: 'app-dashboard',
    templateUrl: 'dashboard.page.component.html',
    styleUrls: ['dashboard.page.component.css']
})
export class DashboardPageComponent implements OnInit, OnDestroy {

    private sub: Subscription | undefined;
    public filter: DashboardFilter = {};
    public filteredDateStart: Date | undefined;
    public filteredDateEnd: Date | undefined;

    public statusCounts$: BehaviorSubject<StatusCounts> = new BehaviorSubject<StatusCounts>({
        activeItemsCount: 0,
        closeRate: 0,
        closedItemsCount: 0,
        openItemsCount: 0
    });

    private get currentUserId() {
        if (this.store.value.currentUser) {
            return this.store.value.currentUser.id;
        } else {
            return undefined;
        }
    }

    constructor(
        private dashboardService: DashboardService,
        private store: Store
    ) { }

    public ngOnInit() {
        this.refresh();
    }

    public onMonthRangeTap(months: number) {
        const range = this.getDateRange(months);
        this.filteredDateStart = range.dateStart;
        this.filteredDateEnd = range.dateEnd;
        this.filter = {
            userId: this.filter.userId,
            dateEnd: range.dateEnd,
            dateStart: range.dateStart
        };
        this.refresh();
    }

    private refresh() {
        this.sub = this.dashboardService.getStatusCounts(this.filter)
            .subscribe(result => {
                this.statusCounts$.next(result);
            });
    }


    private getDateRange(months: number): DateRange {
        const now = new Date();
        const start = new Date();
        start.setMonth(start.getMonth() - months);
        return {
            dateStart: start,
            dateEnd: now
        };
    }

    public ngOnDestroy() {
        if (this.sub) {
            this.sub.unsubscribe();
        }
    }
}
<div class="dashboard">
  <div
    class="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3"
  >
    <div class="col-md order-md-first text-center text-md-left">
      <h2>
        <span class="small text-uppercase text-muted d-block">Statistics</span>
        <span *ngIf="filteredDateStart && filteredDateEnd"
          >{{ filteredDateStart | date }} - {{ filteredDateEnd | date }}</span
        >
      </h2>
    </div>

    <div class="btn-toolbar mb-2 mb-md-0">
      <div class="btn-group mr-2">
        <button type="button" (click)="onMonthRangeTap(3)">3 Month</button>
        <button type="button" (click)="onMonthRangeTap(6)">6 Months</button>
        <button type="button" (click)="onMonthRangeTap(12)">1 Year</button>
      </div>
    </div>
  </div>

  <app-active-issues [statusCounts]="statusCounts$ | async"></app-active-issues>
</div>
